mapper.insert:
INSERT INTO contents (writer, date, detail, file, category)
VALUES (#{writer}, #{date}, #{detail}, #{file}, #{categoryno});
INSERT INTO contents_info (memberid, date, detail, file, category)
VALUES (#{writer}, #{date}, #{detail}, #{file}, #{categoryno});

mapper.update:
UPDATE contents
SET detail = #{detail},
    file = #{file},
    category = #{categoryno}
WHERE no = #{contentsno}
